INSERT INTO `email_templates` (`id`, `date_entered`, `date_modified`, `modified_user_id`, `created_by`, `published`, `name`, `description`, `subject`, `body`, `body_html`, `deleted`, `assigned_user_id`, `text_only`, `type`) VALUES
('d3b49c38-eeba-b910-2590-5b4069caaac1', NOW(), NOW(), '1', '1', NULL, 'Ejemplo - Donación', 'Ejemplo de plantilla de correo electrónico para confirmar y agradecer una donación', '¡Gracias por tu donación!', 'Apreciado/a $contact_first_name $contact_last_name:\r\n\r\nHemos recibido tu donación de $stic_payments_amount €.\r\n\r\n¡Muchas gracias por tu apoyo!\r\n\r\n', '<p>Apreciado/a $contact_first_name $contact_last_name:</p>\n<p>Hemos recibido tu donación de $stic_payments_amount €.</p>\n<p>¡Muchas gracias por tu apoyo!</p>', 0, '1', 0, 'email'),
('dba65685-d26f-6ecd-802c-5b406983c9b5', NOW(), NOW(), '1', '1', NULL, 'Ejemplo - Inscripción', 'Ejemplo de plantilla de correo electrónico para confirmar la inscripción a un evento', '$stic_events_name: Inscripción confirmada', 'Apreciado/a $contact_first_name $contact_last_name:\r\n\r\nHemos recibido tu inscripción a $stic_events_name.\r\n\r\nA día $stic_registrations_registration_date tu inscripción consta como $stic_registrations_status.\r\n\r\n¡Nos vemos el $stic_events_start_date!', '<p>Apreciado/a $contact_first_name $contact_last_name:</p>\n<p>Hemos recibido tu inscripción a $stic_events_name.</p>\n<p>A día $stic_registrations_registration_date tu inscripción consta como $stic_registrations_status.</p>\n<p>¡Nos vemos el $stic_events_start_date!</p>', 0, '1', 0, 'email'),
('52cdc554-bd90-f8f9-e2e0-66d718ae8fe9', NOW(), NOW(), '1', '1', NULL, 'Ejemplo - Nueva subvención', 'Ejemplo de plantilla de correo electrónico para notificar una nueva convocatoria de subvención', 'Nueva convocatoria de subvención: $opportunity_name', 'Nueva convocatoria de subvención: $opportunity_name\r\n\r\n$opportunity_stic_additional_information_c\r\nPuede encontrar más información en: $opportunity_stic_opportunity_url_c', '<p>Nueva convocatoria de subvención: $opportunity_name</p><p></p><p>$opportunity_stic_additional_information_c</p><p>Puede encontrar más información en: $opportunity_stic_opportunity_url_c</p>', 0, '1', 0, 'notification'),
('9f118e85-13f2-baa9-4ee3-66d80c674dea', NOW(), NOW(), '1', '1', NULL, 'Ejemplo - Nuevo evento', 'Ejemplo de plantilla de correo electrónico para notificar un nuevo evento', 'Nuevo evento: $stic_events_name', '$stic_events_type: $stic_events_name\r\n\r\nFecha de inicio: $stic_events_start_date\r\nFecha de finalización: $stic_events_end_date\r\nUbicación: $stic_events_stic_events_fp_event_locations_name', '<p>$stic_events_type: $stic_events_name</p><p></p><p>Fecha de inicio: $stic_events_start_date</p><p>Fecha de finalización: $stic_events_end_date</p><p>Ubicación: $stic_events_stic_events_fp_event_locations_name</p>', 0, '1', 0, 'notification'),
('000005f1-2e4e-3b11-051f-68e3c9e70330', NOW(), NOW(), '1', '1', 'off', 'Ejemplo - Solicitud de firma', 'Plantilla para notificar al firmante que se ha solicitado su firma en un documento.', '[Firma de documento] Solicitud de firma: $stic_signatures_pdf_template', 'Solicitud de firma\n\nSe ha solicitado su firma en el siguiente documento:\n\n$stic_signatures_pdf_template\n\nFirmante: $contact_full_name$contact_user_full_name\nFecha de caducidad: $stic_signatures_expiration_date\n\nPara firmar el documento, haga clic a continuación y se le redirigirá al portal de firma segura.\n\nFirmar el documento: $sugarurl/index.php?entryPoint=sticSign&signatureId=$stic_signatures_id&targetId=$contact_id$contact_user_id\n\nSi no puede acceder al portal de firma, copie y pegue el enlace en su navegador:\n$sugarurl/index.php?entryPoint=sticSign&signatureId=$stic_signatures_id&targetId=$contact_id$contact_user_id', '<!DOCTYPE html>\n<html>\n<head>\n</head>\n<body>\n<p> </p>\n<div style="max-width: 600px; margin: 20px auto; background-color: #ffffff; border-radius: 8px; box-shadow: 0 4px 8px rgba(0,0,0,0.1); overflow: hidden;">\n<div style="background-color: #b5bc31; color: #000000; padding: 20px; text-align: center;">\n<h1 style="margin: 0; font-size: 24px;">Firma de documento</h1>\n</div>\n<div style="padding: 20px;">\n<p style="font-size: 16px; color: #555555;">Se ha solicitado su firma en el siguiente documento:</p>\n<div style="background-color: #f9f9f9; padding: 15px; border-radius: 6px; border: 1px solid #eeeeee; margin-top: 20px;">\n<p style="margin: 0; font-size: 16px; color: #333333;"><strong>$stic_signatures_pdf_template</strong></p>\n<p style="margin: 5px 0 0 0; font-size: 14px; color: #888888;"><strong>Firmante:</strong> $contact_full_name$contact_user_full_name</p>\n<p style="margin: 5px 0 0 0; font-size: 14px; color: #888888;"><strong>Fecha de caducidad:</strong> $stic_signatures_expiration_date</p>\n</div>\n<p style="font-size: 16px; color: #555555; margin-top: 20px;">Para firmar el documento, haga clic a continuación y se le redirigirá al portal de firma segura.</p>\n<p style="font-size: 16px; color: #555555; margin-top: 20px;"> </p>\n<div style="text-align: center; margin-top: 30px;"><a style="display: inline-block; padding: 12px 24px; font-size: 16px; color: #000000; background-color: #b5bc31; border-radius: 5px; text-decoration: none; font-weight: bold;" href="$sugarurl/index.php?entryPoint=sticSign&signatureId=$stic_signatures_id&targetId=$contact_id$contact_user_id">Firmar el documento</a></div>\n<p style="font-size: 14px; color: #aaaaaa; text-align: center; margin-top: 30px;">Si no puede acceder al portal de firma, copie y pegue el enlace en su navegador: <br /><a style="color: #b5bc31; text-decoration: none;" href="$sugarurl/index.php?entryPoint=sticSign&signatureId=$stic_signatures_id&targetId=$contact_id$contact_user_id">$sugarurl/index.php?entryPoint=sticSign&signatureId=$stic_signatures_id&targetId=$contact_id$contact_user_id</a></p>\n</div>\n</div>\n</body>\n</html>', 0, '1', 0, 'notification'),
('000005f1-2e4e-3b11-051f-68e3c9e70331', NOW(), NOW(), '1', '1', 'off', 'Ejemplo - Documento firmado', 'Plantilla para enviar al firmante el documento firmado.', '[Firma de documento] Documento firmado: $stic_signatures_pdf_template', 'Documento firmado\n\nAdjunto a este correo encontrará la versión final del documento que ha firmado:\n\n$stic_signatures_pdf_template\n\nFirmante: $contact_full_name$contact_user_full_name\nFecha de la firma: $stic_signers_signature_date\n\nEn el portal de firma podrá acceder al documento firmado siempre que sea necesario.\n\nIr al portal de firma: $sugarurl/index.php?entryPoint=sticSign&signerId=$stic_signers_id', '<!DOCTYPE html>\n<html>\n<head>\n</head>\n<body>\n<p> </p>\n<div style="max-width: 600px; margin: 20px auto; background-color: #ffffff; border-radius: 8px; box-shadow: 0 4px 8px rgba(0,0,0,0.1); overflow: hidden;">\n<div style="background-color: #b5bc31; color: #000000; padding: 20px; text-align: center;">\n<h1 style="margin: 0; font-size: 24px;">Documento firmado</h1>\n</div>\n<div style="padding: 20px;">\n<p style="font-size: 16px; color: #555555;">Adjunto a este correo encontrará la versión final del documento que ha firmado:</p>\n<div style="background-color: #f9f9f9; padding: 15px; border-radius: 6px; border: 1px solid #eeeeee; margin-top: 20px;">\n<p style="margin: 0; font-size: 16px; color: #333333;"><strong>$stic_signatures_pdf_template</strong></p>\n<p style="margin: 5px 0 0 0; font-size: 14px; color: #888888;"><strong>Firmante:</strong> $contact_full_name$contact_user_full_name</p>\n<p style="margin: 5px 0 0 0; font-size: 14px; color: #888888;"><strong>Fecha de la firma:</strong> $stic_signers_signature_date</p>\n</div>\n<p style="font-size: 16px; color: #555555; margin-top: 20px;">En el portal de firma podrá acceder al documento firmado siempre que sea necesario.</p>\n<div style="text-align: center; margin-top: 30px;"><a style="display: inline-block; padding: 12px 24px; font-size: 16px; color: #000000; background-color: #b5bc31; border-radius: 5px; text-decoration: none; font-weight: bold;" href="$sugarurl/index.php?entryPoint=sticSign&signerId=$stic_signers_id">Ir al portal de firma</a></div>\n</div>\n</div>\n</body>\n</html>', 0, '1', 0, 'notification'),
('000005f1-2e4e-3b11-051f-68e3c9e70332', NOW(), NOW(), '1', '1', 'off', 'Ejemplo - Código de un solo uso para firmar', 'Plantilla para enviar el código de un solo uso al firmante antes de poder firmar.', '[Firma de documento] Código de verificación para firmar', 'Verificación de firma\n\nPara garantizar la seguridad y autenticidad de su firma, utilice el código de verificación que se muestra a continuación:\n\n$stic_signatures_pdf_template\n\n$stic_signers_otp\n\nIntroduzca este código en el portal de firma para proceder. El código es válido durante 10 minutos y solo se puede usar una vez.', '<!DOCTYPE html>\n<html>\n<head>\n</head>\n<body>\n<p> </p>\n<div style="max-width: 600px; margin: 20px auto; background-color: #ffffff; border-radius: 8px; box-shadow: 0 4px 8px rgba(0,0,0,0.1); overflow: hidden;">\n<div style="background-color: #b5bc31; color: #000000; padding: 20px; text-align: center;">\n<h1 style="margin: 0; font-size: 24px;">Verificación de firma</h1>\n</div>\n<div style="padding: 20px;">\n<p style="font-size: 16px; color: #555555;">Para garantizar la seguridad y autenticidad de su firma, utilice el código de verificación que se muestra a continuación:</p>\n<div style="background-color: #f9f9f9; padding: 15px; border-radius: 6px; border: 1px solid #eeeeee; margin-top: 20px;">\n<p style="margin: 0; font-size: 16px; color: #333333;"><strong>$stic_signatures_pdf_template</strong></p>\n<div style="text-align: center; margin-top: 10px; margin-bottom: 10px;"><span style="display: inline-block; padding: 15px 30px; font-size: 28px; color: #000000; background-color: #b5bc31; border-radius: 5px; font-weight: bold; letter-spacing: 5px;">$stic_signers_otp</span></div>\n</div>\n<p style="font-size: 16px; color: #555555; margin-top: 20px;">Introduzca este código en el portal de firma para proceder. El código es válido durante 10 minutos y solo se puede usar una vez.</span></p>\n</div>\n</div>\n</body>\n</html>', 0, '1', 0, 'notification'),
('000005f1-2e4e-3b11-051f-68e3c9e70333', NOW(), NOW(), '1', '1', 'off', 'Ejemplo - Código de un solo uso para firmar (SMS)', 'Plantilla para enviar vía SMS el código de un solo uso al firmante antes de poder firmar', '[Firma de documento] Código de verificación para firmar', '$contact_first_name$contact_user_first_name, este es su código para firmar: $stic_signers_otp. Es válido durante 10 minutos.', '<!DOCTYPE html>\n<html>\n<head>\n</head>\n<body>\n\n</body>\n</html>', 0, '1', 1, 'system');
INSERT INTO `email_templates` (`id`, `date_entered`, `date_modified`, `modified_user_id`, `created_by`, `published`, `name`, `description`, `subject`, `body`, `body_html`, `deleted`, `assigned_user_id`, `text_only`, `type`) VALUES
('14f18ded-378d-ab84-3237-5e830d4e874d', NOW(), NOW(), '1', '1', 'off', 'Caso actualizado (Usuarios)', 'Plantilla para informar a un usuario del CRM cuando se actualiza un caso que tiene asignado.', 'Actualización de: $acase_name (# $acase_case_number)', 'Hola, $user_first_name $user_last_name:\n\nSe ha actualizado el caso $acase_name (# $acase_case_number) el $aop_case_updates_date_entered.\n\n$contact_first_name $contact_last_name dice:\n\n	$aop_case_updates_description\n\nPuedes revisar el caso en: $sugarurl/index.php?module=Cases&action=DetailView&record=$acase_id', '<p>Hola, $user_first_name $user_last_name:</p>\n<p> </p>\n<p>Se ha actualizado el caso $acase_name (# $acase_case_number) el $aop_case_updates_date_entered.</p>\n <p><strong>$contact_first_name $contact_last_name dice:</strong></p>\n<p style="padding-left:30px;">$aop_case_updates_description</p>\n <p>Puedes revisar el caso en: $sugarurl/index.php?module=Cases&action=DetailView&record=$acase_id</p>', 0, 1, NULL, 'system'),
('267275d8-959c-6aed-8754-5e830d07fedf', NOW(), NOW(), '1', '1', 'off', 'Invitación a un evento', 'Plantilla de invitación a un evento.', 'Invitación a $fp_events_name', 'Apreciado/a $contact_name:\nHas sido invitado/a a $fp_events_name del $fp_events_date_start al $fp_events_date_end.\n$fp_events_description\nCordialmente,\n', '\n<p>Apreciado/a $contact_name:</p>\n<p>Has sido invitado/a a $fp_events_name del $fp_events_date_start al $fp_events_date_end.</p>\n<p>$fp_events_description</p>\n<p>Para aceptar esta invitación haz clic en Acepto.</p>\n<p> $fp_events_link o $fp_events_link_declined</p>\n<p>Cordialmente,</p>\n', 0, 1, NULL, 'system'),
('483e1c3b-51dc-b5ab-e220-5e830d52596a', NOW(), NOW(), '1', '1', 'off', 'Confirmación de alta voluntaria', 'Plantilla para confirmar el alta voluntaria de una persona.', 'Confirmación de alta voluntaria', 'Hola, $contact_first_name $contact_last_name:\nPor favor, confirma tu alta voluntaria haciendo clic en el enlace siguiente: $sugarurl/index.php?entryPoint=ConfirmOptIn&from=$emailaddress_email_address', '<p>Hola, $contact_first_name $contact_last_name:</p>\n <p>Por favor, confirma tu alta voluntaria haciendo clic en el enlace siguiente:\n <a href="$sugarurl/index.php?entryPoint=ConfirmOptIn&from=$emailaddress_confirm_opt_in_token">Alta voluntaria</a></p>\n', 0, 1, NULL, 'system'),
('b4477938-f3c4-bef6-292c-5e830dee6ceb', NOW(), NOW(), '1', '1', 'off', 'Contraseña generada por el sistema', 'Plantilla para notificar una contraseña a un usuario.', 'Información de la nueva cuenta', 'Aquí tienes tu nombre de usuario y una contraseña provisional:\nNombre de usuario: $contact_user_user_name\nContraseña: $contact_user_user_hash\n\n$config_site_url\n\nAl acceder al sistema con esta contraseña es posible que debas cambiarla por otra de tu elección.', '<div><table width="550"><tbody><tr><td><p>Aquí tienes tu nombre de usuario y una contraseña provisional:</p>\n <p>Nombre de usuario: $contact_user_user_name</p>\n <p>Contraseña: $contact_user_user_hash</p>\n <p>$config_site_url</p>\n <p>Al acceder al sistema con esta contraseña es posible que debas cambiarla por otra de tu elección.</p></td></tr></tbody></table></div>', 0, 1, NULL, 'system'),
('bc4c87c3-febd-73d5-cf73-5e830dd3bac0', NOW(), NOW(), '1', '1', 'off', 'Contraseña olvidada', 'Plantilla para regenerar la contraseña de acceso al CRM.', 'Resetea tu contraseña', 'El $contact_user_pwd_last_changed has solicitado poder resetear tu contraseña.\n\nHaz clic en el enlace siguiente para resetearla: $contact_user_link_guid', '<div><table width="550"><tbody><tr><td><p>El $contact_user_pwd_last_changed has solicitado poder resetear tu contraseña.</p>\n <p>Haz clic en el enlace siguiente para resetearla: $contact_user_link_guid</p></td></tr></tbody></table></div>', 0, 1, NULL, 'system'),
('c4440a38-10b9-ef63-0e40-5e830d1c293d', NOW(), NOW(), '1', '1', 'off', 'Doble factor de autenticación', 'Plantilla para enviar un código de doble factor de autenticación.', 'Código de doble factor de autenticación', 'El código de doble factor de autenticación es $code.', '<div><table width="550"><tbody><tr><td><p>El código de doble factor de autenticación es <b>$code</b>.</p></td></tr></tbody></table></div>', 0, 1, NULL, 'system'),
('c6c4093f-0a1a-30d8-5e33-5e830d6ddd51', NOW(), NOW(), '1', '1', 'off', 'Caso actualizado (Personas)', 'Plantilla para enviar a una persona cuando se actualiza un caso con el que está relacionada', 'Actualización del caso: $acase_name [$acase_case_number]', 'Hola, $contact_first_name $contact_last_name:\n\nSe ha actualizado el caso $acase_name (# $acase_case_number) el $aop_case_updates_date_entered.\n\n$contact_first_name $contact_last_name dice:\n\n	$aop_case_updates_description', '<p>Hola, $contact_first_name $contact_last_name:</p>\n	<p> </p>\n <p>Se ha actualizado el caso $acase_name (# $acase_case_number) el $aop_case_updates_date_entered.</p>\n	<p><strong>$user_first_name $user_last_name dice:</strong></p>\n <p style="padding-left:30px;">$aop_case_updates_description</p>', 0, 1, NULL, 'system'),
('e61c85c2-e6cb-6dda-3760-5e830d31fbdc', NOW(), NOW(), '1', '1', 'off', 'Caso cerrado', 'Plantilla para informar a una persona que se ha cerrado un caso con el cual estaba relacionada.', 'Caso cerrado: $acase_name [$acase_case_number]', 'Hola, $contact_first_name $contact_last_name:\n\nEl caso $acase_name (# $acase_case_number) se ha cerrado el $acase_date_entered.\n\n	Estado:	$acase_status\n	Código:	$acase_case_number\n	Resolución:	$acase_resolution', '<p>Hola, $contact_first_name $contact_last_name:</p>\n	<p>El caso $acase_name (# $acase_case_number) se ha cerrado el $acase_date_entered</p>\n <table border="0"><tbody><tr><td>Estado</td><td>$acase_status</td></tr><tr><td>Código</td><td>$acase_case_number</td></tr><tr><td>Resolución</td><td>$acase_resolution</td></tr></tbody></table>', 0, 1, NULL, 'system'),
('e9e00ea2-ac1b-a635-9671-5e830d66a582', NOW(), NOW(), '1', '1', 'off', 'Caso creado', 'Plantilla para enviar a una persona cuando esta abre un caso.', 'Nuevo caso: $acase_name [$acase_case_number]', 'Hola, $contact_first_name $contact_last_name:\n\nHemos recibido tu caso $acase_name (# $acase_case_number) el $acase_date_entered.\n\n	Estado:	$acase_status\n	Código:	$acase_case_number\n	Descripción:	$acase_description', '<p>Hola, $contact_first_name $contact_last_name:</p>\n <p>Hemos recibido tu caso $acase_name (# $acase_case_number) el $acase_date_entered</p>\n <table border="0"><tbody><tr><td>Estado</td><td>$acase_status</td></tr><tr><td>Código</td><td>$acase_case_number</td></tr><tr><td>Descripción</td><td>$acase_description</td></tr></tbody></table>', 0, 1, NULL, 'system'),
('ed9867a2-1f56-b684-4bc6-5e830d215b5d', NOW(), NOW(), '1', '1', 'off', 'Cuenta creada', 'Plantilla para informar a una persona que se ha creado su cuenta del portal de casos.', 'Cuenta creada en el portal de soporte', 'Hola, $contact_name:\n\nYa dispones de una cuenta para acceder a $portal_address.\n\nPuedes acceder a él usando tu dirección de correo electrónico y la contraseña $joomla_pass.', '<p>Hola, $contact_name:</p>\n <p>Ya dispones de una cuenta para acceder a <a href="$portal_address">$portal_address</a>.</p>\n <p>Puedes acceder a él usando tu dirección de correo electrónico y la contraseña $joomla_pass.</p>', 0, 1, NULL, 'system');
INSERT INTO `email_templates` (`id`, `date_entered`, `date_modified`, `modified_user_id`, `created_by`, `published`, `name`, `description`, `subject`, `body`, `body_html`, `deleted`, `assigned_user_id`, `text_only`, `type`) VALUES
('00000b6d-9a8d-37c0-684e-6931622133d8', NOW(), NOW(), '1', '1', 'off', 'Notificación de la renovación de la póliza de seguros', NULL, 'Renovación de la póliza de seguro de voluntariado', NULL, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p data-start=\"262\" data-end=\"301\">La <strong data-start=\"294\" data-end=\"333\">póliza de seguro</strong> <strong>de voluntariado</strong> de $contact_name está próxima a vencer y requiere renovación para mantener la cobertura activa.</p>\r\n</body>\r\n</html>', 0, '1', 0, 'email'),
('00000258-e3da-390c-e722-693162f2060f', NOW(), NOW(), '1', '1', 'off', 'Notificación de la renovación del certificado de delitos', NULL, 'Renovación del Certificado de Delitos', NULL, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p data-start=\"303\" data-end=\"457\">El <strong data-start=\"324\" data-end=\"350\">Certificado de Delitos</strong> de $contact_name está próximo a vencer y requiere ser renovado para que pueda seguir desarrollando su actividad en nuestra organización. Debería iniciarse el proceso de renovación a la mayor brevedad posible.</p>\r\n</body>\r\n</html>', 0, '1', 0, 'email');