-- SinergiaCRM: Ejemplos de Plantillas PDF
REPLACE INTO `aos_pdf_templates` (`id`, `name`, `date_entered`, `date_modified`, `modified_user_id`, `created_by`, `description`, `deleted`, `assigned_user_id`, `active`, `type`, `pdfheader`, `pdffooter`, `margin_left`, `margin_right`, `margin_top`, `margin_bottom`, `margin_header`, `margin_footer`, `page_size`, `orientation`) VALUES
('49e6e278-9584-b17c-6e41-6414358fafde', 'Ejemplo - Personas - 01 - Cesión de Datos (RGPD)', NOW(), NOW(), '1', '1', '<h1 align=\"center\"><span style=\"font-size:2em;\"> </span><img style=\"font-size:2em;\" src=\"https://sinergiacrm.org/wp-content/uploads/2019/04/cropped-Logo_SinergiaTIC-5.png\" alt=\"\" width=\"375\" height=\"113\" /></h1><h1 align=\"center\"><strong>DOCUMENTO DE CESIÓN DE DATOS PERSONALES</strong></h1><p align=\"center\">  </p><p style=\"text-align:justify;\"><span style=\"color:#b4bc32;\"><strong>$contacts_name</strong></span>, con domicilio en <span style=\"color:#b4bc32;\"><strong>$contacts_primary_address_street</strong></span> de <strong><span style=\"color:#b4bc32;\">$contacts_primary_address_city</span></strong> y con <span style=\"color:#b4bc32;\"><strong>$contacts_stic_identification_type_c</strong></span> n.º <span style=\"color:#b4bc32;\"><strong>$contacts_stic_identification_number_c</strong><span style=\"color:#333300;\">:</span></span></p><p style=\"text-align:justify;\">Autoriza que sus datos personales sean tratados por <span style=\"color:#333300;\"><strong>Nombre Organización</strong></span><em><strong> </strong></em>con domicilio en <strong>Dirección Organización</strong> y NIF <strong>NIF Organización</strong> con la finalidad de gestionar su donación e informarle de las actividades de la organización.</p><p style=\"text-align:justify;\">Asimismo, sus datos personales podrán ser cedidos a las Administraciones Públicas en cumplimiento con la normativa legal vigente.</p><p style=\"text-align:justify;\">Adicionalmente, <strong>Nombre Organización</strong> podrá ceder sus datos a aquellas empresas u organizaciones que prestan servicio a <strong>Nombre Organización </strong>y requieran de los mismos para realizar su labor.</p><p style=\"text-align:justify;\">Puede ejercer sus derechos de acceso, rectificación, cancelación u oposición ante <strong>Nombre Organización</strong> en la dirección anteriormente indicada mediante solicitud escrita acompañada de copia del DNI.</p><p style=\"text-align:justify;\">Para consultar información más detallada acerca del uso, finalidades y derechos que le amparan puede consultar la página <a title=\"http://laorganizacion.com/rgpd\" href=\"http://laorganizacion.com/rgpd\">http://laorganizacion.com/rgpd</a>.</p><p> </p><p>En <span style=\"color:#b4bc32;\"><strong>$contacts_primary_address_city</strong></span> a {DATE d/m/Y}, </p><p> </p><p>Firma del interesado/a</p><p> </p><p> </p><p> </p>', 0, '1', 1, 'Contacts', NULL, '<p style=\"text-align:center;\" align=\"right\"><em><span style=\"font-size:x-small;\"><strong>p.{PAGENO}</strong></span></em></p>', 30, 30, 15, 15, 9, 9, 'A4', 'Portrait'),
('a35d5775-7805-0e10-e5c6-641435f2d02b', 'Ejemplo - Personas - 02 - Certificado de donaciones anual', NOW(), NOW(), '1', '1', '<h1 align=\"center\"><span style=\"font-size:2em;\"> </span><img style=\"font-size:2em;\" src=\"https://sinergiacrm.org/wp-content/uploads/2019/04/cropped-Logo_SinergiaTIC-5.png\" alt=\"\" width=\"375\" height=\"113\" /></h1><h1 align=\"center\">CERTIFICADO DE DONACIONES</h1><p style=\"padding-left:30px;\" align=\"center\"> </p><p style=\"padding-left:30px;text-align:justify;\">D./D.ª <strong>Nombre de la persona que representa a la organización</strong>, con DNI <strong>DNI de dicha persona</strong>, en su condición de representante de <strong>Nombre Organización</strong> con domicilio en <strong>Localidad</strong>, con NIF <strong>NIF Organización</strong>.  </p><p style=\"padding-left:30px;\"> </p><p style=\"padding-left:30px;\"><strong style=\"text-align:justify;\">CERTIFICA</strong></p><p style=\"padding-left:30px;\"> </p><p style=\"padding-left:30px;text-align:justify;\"><strong>PRIMERO</strong>.- Que esta entidad, constituida con fecha <strong>XX/XX/XXXX</strong>, se encuentra incluida en las reguladas por el artículo 16 de la Ley 49/2002 (entidades beneficiarias del mecenazgo).</p><p style=\"padding-left:30px;text-align:justify;\"> </p><p style=\"padding-left:30px;text-align:justify;\"><strong>SEGUNDO</strong>.- Que para ayudar al cumplimiento de los fines estatutarios de la entidad <strong>Nombre Organización</strong>, D/Dª. <span style=\"color:#808000;\"><strong>$contacts_name</strong></span>, con <strong><span style=\"color:#808000;\">$contacts_stic_identification_type_c  $contacts_stic_identification_number_c</span></strong>, y residente en la provincia de <strong><span style=\"color:#808000;\">$contacts_primary_address_state</span></strong> ha abonado a esta organización con carácter irrevocable la cantidad de <strong><span style=\"color:#808000;\">$contacts_stic_total_annual_donations_c €</span></strong> a lo largo del año 2022.</p><p style=\"text-align:justify;padding-left:30px;\"> </p><p style=\"padding-left:30px;\">En <strong>Localidad</strong>, a {DATE d/m/Y} </p><p> </p>', 0, '1', 1, 'Contacts', NULL, '<p style=\"text-align:center;\" align=\"right\"><em><span style=\"font-size:x-small;\"><strong>p.{PAGENO}</strong></span></em></p>', 30, 30, 15, 15, 9, 9, 'A4', 'Portrait'),
-- ('adf40f80-f8d7-4cff-0ed1-63b6ed256afd', 'Ejemplo - Facturas - 01 - Factura a organización', NOW(), NOW(), '1', '1', '<table style=\"width:100%;font-family:Arial;text-align:center;\" border=\"0\" cellspacing=\"2\" cellpadding=\"2\"><tbody style=\"text-align:left;\"><tr style=\"text-align:left;\"><td style=\"text-align:left;\"><p><img style=\"float:left;\" src=\"https://sinergiacrm.org/wp-content/uploads/2019/04/cropped-Logo_SinergiaTIC-5.png\" alt=\"\" width=\"220\" /> </p></td></tr><tr style=\"text-align:left;\"><td style=\"font-weight:bold;text-align:left;\"><div dir=\"ltr\" align=\"left\"><table><colgroup><col width=\"800\" /><col width=\"329\" /></colgroup><tbody><tr><td><p dir=\"ltr\"><strong><span style=\"font-size:small;\">Nombre de la Organización</span></strong></p></td></tr><tr><td><p dir=\"ltr\"><strong><span style=\"font-size:small;\">Dirección</span></strong></p></td></tr><tr><td><p dir=\"ltr\"><strong><span style=\"font-size:small;\">CP Municipio</span></strong></p></td></tr><tr><td><p dir=\"ltr\"><span style=\"font-size:small;\"><strong>NIF</strong></span></p></td></tr></tbody></table></div></td></tr></tbody></table><p> </p><table style=\"width:619px;border:0pt none;border-spacing:0pt;height:82px;\"><tbody style=\"text-align:left;\"><tr><td style=\"font-weight:bold;text-align:center;\">FACTURA</td></tr><tr style=\"text-align:left;\"><td style=\"font-weight:bold;padding:2px 6px;border-style:solid;border-width:0.5px;vertical-align:top;width:50%;text-align:center;\">Número factura</td><td style=\"font-weight:bold;padding:2px 6px;border-style:solid;border-width:0.5px;vertical-align:top;width:50%;text-align:center;\"> <span>Fecha factura</span></td></tr><tr style=\"text-align:left;\"><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:50%;vertical-align:top;text-align:center;\"><div>$aos_invoices_number</div></td><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:50%;vertical-align:top;text-align:center;\"> $aos_invoices_invoice_date</td></tr></tbody></table><p> </p><table><tbody><tr style=\"text-align:left;\"><td><strong>FACTURAR A:</strong></td></tr><tr style=\"text-align:left;\"><td style=\"padding:2px 6px;width:50%;vertical-align:top;text-align:left;\"><div><div><span>$aos_invoices_billing_contact$aos_invoices_billing_account</span></div><div><span>$billing_contact_stic_identification_number_c$billing_account_stic_identification_number_c</span></div><div><span>$billing_contact_primary_address_street $billing_account_billing_address_street</span></div><div><span>$billing_contact_primary_address_postalcode $billing_contact_primary_address_city $billing_account_billing_address_postalcode $billing_account_billing_address_city</span></div><div><span>$billing_contact_primary_address_state $billing_account_billing_address_state</span></div> </div></td></tr></tbody></table><table style=\"width:100%;border:0pt none;border-spacing:0pt;\"><tbody><tr><td><strong>DESCRIPCIÓN</strong></td></tr><tr><td>$aos_invoices_description</td></tr></tbody></table><p> </p><table style=\"width:566px;border:0pt none;border-spacing:0pt;height:132px;\"><tbody><tr><td><strong>IMPORTES</strong></td></tr><tr><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:75%;vertical-align:top;text-align:left;\"><div>Total antes de impuestos</div></td><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:25%;vertical-align:top;text-align:right;\">$aos_invoices_subtotal_amount €</td></tr><tr><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:75%;vertical-align:top;text-align:left;\"><div>IVA</div></td><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:25%;vertical-align:top;text-align:right;\">$aos_invoices_tax_amount €</td></tr><tr><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:75%;vertical-align:top;text-align:left;\"><div><strong>TOTAL</strong></div></td><td style=\"padding:2px 6px;border-style:solid;border-width:0.5px;width:25%;vertical-align:top;text-align:right;\">$aos_invoices_total_amount €</td></tr></tbody></table><p> </p><table style=\"width:656px;border:0pt none;border-spacing:0pt;height:90px;\"><tbody><tr><td><strong>FORMA DE PAGO</strong></td></tr><tr><td>Transferencia bancaria a BANCO ESXX XXXX XXXX XXXX XXXX XXXX</td></tr><tr><td><p>Vencimiento: $aos_invoices_due_date</p></td></tr></tbody></table><p> </p><div id=\"sugar_text_gtx-trans\"> </div>', 0, '1', 1, 'AOS_Invoices', NULL, '<p style=\"text-align:right;\"><span style=\"font-size:x-small;\"><em><strong>p.{PAGENO}</strong></em></span></p>', 15, 15, 16, 16, 5, 9, 'A4', 'Portrait'),
('cc2864ec-85aa-6838-dcd2-63b6ac981294', 'Ejemplo - Organizaciones - 01 - Certificado de donaciones anual', NOW(), NOW(), '1', '1', '<h1 align=\"center\"><span style=\"font-size:2em;\"> </span><img style=\"font-size:2em;\" src=\"https://sinergiacrm.org/wp-content/uploads/2019/04/cropped-Logo_SinergiaTIC-5.png\" alt=\"\" width=\"375\" height=\"113\" /></h1><h1 align=\"center\"><strong>CERTIFICADO DE DONACIONES</strong></h1><p align=\"center\"><span style=\"text-align:justify;\"> </span></p><p style=\"text-align:justify;padding-left:30px;\"><span>D./D.ª </span><strong>Nombre de la persona que representa a la organización</strong><span>, con DNI </span><strong>DNI de dicha persona</strong><span>, en su condición de representante de </span><strong>Nombre Organización</strong><span> con domicilio en </span><strong>Localidad</strong><span>, con NIF </span><strong>NIF Organización</strong><span>.</span></p><p style=\"text-align:justify;\"> </p><p style=\"text-align:justify;padding-left:30px;\"><strong>CERTIFICA</strong></p><p style=\"text-align:justify;padding-left:30px;\"> </p><p style=\"text-align:justify;padding-left:30px;\"><strong>PRIMERO</strong>.- Que esta entidad, constituida con fecha <strong>XX/XX/XXXX</strong>, se encuentra incluida en las reguladas en el artículo 16 de la Ley 49/2002 (entidades beneficiarias del mecenazgo).</p><p style=\"padding-left:30px;\"> </p><p style=\"text-align:justify;padding-left:30px;\"><strong>SEGUNDO</strong>.- Que para ayudar al cumplimiento de los fines estatutarios de la entidad <strong>Asociación SinergiaTIC</strong>, <span style=\"color:#808000;\"><strong>$accounts_name</strong></span>, con número de identificación fiscal <span style=\"color:#808000;\"><strong>$accounts_stic_identification_number_c</strong></span>, y con domicilio en la provincia de <strong><span style=\"color:#808000;\">$accounts_billing_address_state</span></strong> ha abonado a esta organización con carácter irrevocable la cantidad de <strong><span style=\"color:#808000;\">$accounts_stic_total_annual_donations_c</span><span style=\"color:#808000;\"> €</span></strong> a lo largo del año 2022<strong><em><a>.</a></em></strong></p><p style=\"padding-left:30px;\"> </p><p style=\"padding-left:30px;\">En <strong>Localidad</strong>, a {DATE d/m/Y} </p><p> </p>', 0, '1', 1, 'Accounts', NULL, '<p style=\"text-align:center;\" align=\"right\"><em><span style=\"font-size:x-small;\"><strong>p.{PAGENO}</strong></span></em></p>', 30, 30, 15, 15, 9, 9, 'A4', 'Portrait');
